import React from 'react';
import {
  Box,
  Container,
  Text,
  VStack,
  Input,
  Button,
  Flex,
  Heading,
  useColorModeValue,
} from '@chakra-ui/react';
import { useForm, ValidationError } from '@formspree/react';

const SubscriptionSection = () => {
  const [state, handleSubmit] = useForm("mayrgkba"); // Replace "your_form_id" with your actual Formspree form ID
  if (state.succeeded) {
    return <p>We've received your email and we will not spam!</p>;
  }
  return (
    <Box bgGradient="linear(to-b, white, gray.100)" py="50px">
      <Container maxW="8xl">
        <VStack spacing={4} textAlign="center">
          <Heading fontSize="lg" fontWeight="bold">
            Get to know what we're doing, first
          </Heading>
          <Text>Subscribe to our newsletter to stay updated with our latest events and news.</Text>
          <form onSubmit={handleSubmit}>
            <Flex
              direction="column"
              align="center"
            >
              <Input
                placeholder="Enter your email"
                type="email"
                name="email" // Name attribute should match the field name in your Formspree form
                required
                mb={2}
              />
              <ValidationError 
                prefix="Email" 
                field="email"
                errors={state.errors}
              />
              <Button 
                colorScheme="teal" 
                type="submit" 
                w="full"
                isLoading={state.submitting}
              >
                {state.submitting ? 'Subscribing...' : 'Subscribe'}
              </Button>
            </Flex>
          </form>
        </VStack>
      </Container>
    </Box>
  );
};

export default SubscriptionSection;
