import React from 'react';
import {
  Box,
  Container,
  Text,
  VStack,
  HStack,
  Flex,
  Heading,
  useColorModeValue,
} from '@chakra-ui/react';

const events = [
  {
    id: 1,
    date: 'June 20, 2024',
    title: 'React Conference',
    description: 'Join us for a day of React talks and workshops with industry leaders.',
  },
  {
    id: 2,
    date: 'July 15, 2024',
    title: 'JavaScript Summit',
    description: 'A summit dedicated to all things JavaScript, from frameworks to tooling.',
  },
];

const UpcomingEvents = () => {
  return (
    <Box bgGradient="linear(to-b, white, gray.200)">
        <Box mx="150">
      <Container maxW="8xl">
        <Heading fontSize="lg" fontWeight="bold" mb={10} textAlign="left">
          Upcoming Events
        </Heading>
        <Flex wrap="wrap" justifyContent="left" spacing={8}>
          {events.map((event) => (
            <EventCard key={event.id} {...event} />
          ))}
        </Flex>
      </Container>
      </Box>
    </Box>
  );
};

const EventCard = ({ date, title, description }) => {
  return (
    <Box
      w={{ base: '100%', md: '30%' }}
      bg={useColorModeValue('white', 'gray.700')}
      boxShadow="lg"
      rounded="lg"
      p={6}
      m={4}
      textAlign="left"
    >
      <Text fontSize="m" color="teal.500" mb={2}>
        {date}
      </Text>
      <Heading as="h3" fontSize="m" mb={2}>
        {title}
      </Heading>
      <Text fontSize="md">{description}</Text>
    </Box>
  );
};

const App = () => (
  <div>
    <UpcomingEvents />
  </div>
);

export default App;
