import React from 'react';
import { Flex, Text, Box, Icon, useColorModeValue } from '@chakra-ui/react';

function TimelineRow({ logo, title, date, color, index, arrLength }) {
  const textColor = useColorModeValue('gray.700', 'white.300');
  const bgIconColor = useColorModeValue('white.300', 'gray.700');
  const isLastItem = index === arrLength - 1;

  return (
    <Flex mb={isLastItem ? '0' : '24px'} alignItems="center">
      <Box position="relative">
        <Flex alignItems="center" justifyContent="center" borderRadius="50%" bg={bgIconColor} p="12px">
          <Icon as={logo} w="24px" h="24px" color={color} />
        </Flex>
        {!isLastItem && (
          <Box
            position="absolute"
            top="50%"
            left="50%"
            transform="translate(-50%, 0)"
            w="2px"
            h="24px"
            bg={color}
            zIndex="-1"
          />
        )}
      </Box>
      <Flex direction="column" pl="16px">
        <Text fontSize="md" color={textColor} fontWeight="bold">{title}</Text>
        <Text fontSize="sm" color="gray.400" fontWeight="normal">{date}</Text>
      </Flex>
    </Flex>
  );
}

export default TimelineRow;
