import React from 'react';
import {
  Box,
  Container,
  Text,
  VStack,
  HStack,
  Button,
  Flex,
  Heading,
  useColorModeValue,
} from '@chakra-ui/react';

const events = [
  {
    id: 1,
    date: 'June 20, 2024',
    title: 'React Conference',
    description: 'Join us for a day of React talks and workshops with industry leaders.',
  },
  {
    id: 2,
    date: 'July 15, 2024',
    title: 'JavaScript Summit',
    description: 'A summit dedicated to all things JavaScript, from frameworks to tooling.',
  },
  {
    id: 2,
    date: 'July 15, 2024',
    title: 'JavaScript Summit',
    description: 'A summit dedicated to all things JavaScript, from frameworks to tooling.',
  },
];

const UpcomingEvents = () => {
  return (
    <Box bgGradient="linear(to-b, white, gray.200)" py="50px">
        <Box mx="150">
      <Container maxW="8xl">
      <Heading size="s" as="h2" mb="4" textAlign="left">UPCOMING EVENTS</Heading>
      <Heading size="md" as="h2" mb="3" textAlign="left">What we are brewing for you</Heading>
        <Flex wrap="wrap" justifyContent="flex-start" spacing={1}>
          {events.map((event, index) => (
            <EventCard key={event.id} {...event} isFirst={index === 0} />
          ))}
        </Flex>
      </Container>
      </Box>
    </Box>
  );
};

const EventCard = ({ date, title, description, isFirst }) => {
  return (
    <Box
      w={{ base: '100%', md: '30%' }}
      bg={useColorModeValue('white', 'gray.700')}
      boxShadow="lg"
      rounded="lg"
      p={6}
      m={4}
      ml={isFirst ? 0 : 4}  // Remove left margin for the first card
      textAlign="left"
    >
      <Text fontSize="md" color="#AAF4E3" mb={2}>
        {date}
      </Text>
      <Heading as="h3" fontSize="md" mb={2}>
        {title}
      </Heading>
      <Text fontSize="md">{description}</Text>
      <Button mt="5" bg="#AAF4E3">Register</Button>
    </Box>
  );
};

const App = () => (
  <div>
    <UpcomingEvents />
  </div>
);

export default App;
