import React from 'react';
import { Flex, Heading, Button, ScaleFade, Box, Text } from '@chakra-ui/react';
import CoffeeBrewing from './CoffeeBrewing'; // Import the CoffeeBrewingAnimation component

function LandingPage() {
  return (
    <Flex bg="#F5F5F5" height="80vh" alignItems="center" justifyContent="center" color="white" mb="50px">
      <Box textAlign="center">
        < CoffeeBrewing />
        <ScaleFade initialScale={0.9} in={true}>
          <Heading size="2xl" mb="8" fontWeight="bold" color="black">
            Welcome to Our <Text as="span" style={{ background: "linear-gradient(to top, teal 50%, transparent 50%)" }}>Website</Text>
          </Heading>
          <Button
            colorScheme="teal"
            mr="4"
            size="lg"
            _hover={{ transform: "scale(1.05)" }}
            _active={{ transform: "scale(0.95)" }}
          >
            Get Started
          </Button>
          <Button
            bg="gray.300"
            color="black"
            ml="4"
            size="lg"
            _hover={{ transform: "scale(1.05)", bg: "gray.400" }}
            _active={{ transform: "scale(0.95)" }}
          >
            Learn More
          </Button>
        </ScaleFade>
      </Box>
    </Flex>
  );
}

export default LandingPage;
