import React, { useState, useEffect } from "react";
import {
  ChakraProvider,
  extendTheme,
  Box,
  Flex,
  Text,
  IconButton,
  useColorMode,
  ColorModeProvider,
} from "@chakra-ui/react";
import { Link, animateScroll as scroll } from "react-scroll";
import { Tooltip } from "@chakra-ui/react";
import { SunIcon, MoonIcon } from "@chakra-ui/icons";
import { Button } from "@chakra-ui/react";import Navbar from './components/Navbar';
import LandingPage from './components/LandingPage';


function App() {
  const scrollToTop = () => {
    scroll.scrollToTop({
      duration: 500, // Adjust the scroll duration as needed
      smooth: "easeInOutQuad", // Adjust the scroll animation as needed
    });
  };

  const [theme, setTheme] = useState("dark");
  const toggleTheme = () => {
    if (theme === "light") {
      setTheme("dark");
    } else {
      setTheme("light");
    }
  };
  useEffect(() => {
    document.body.className = theme;
  }, [theme]);
  return (
    <div  className={`App ${theme}`}>
    <ChakraProvider  theme={theme}>
      <div>
        <Navbar />
        <LandingPage />
        {/* Your other components and routes go here */}
      </div>
    </ChakraProvider>
    </div>
  );
}

export default App;
