import React, { useState } from 'react';
import { Box, Flex, Heading, Text, Icon, SimpleGrid } from '@chakra-ui/react';
import { MdFolder, MdFolderOpen } from 'react-icons/md';

const YearSection = () => {
  const [selectedYear, setSelectedYear] = useState(null);
  const [openFolder, setOpenFolder] = useState(null);

  const years = [
    { year: 2024, desc: "Description for year 2024" },
    { year: 2023, desc: "Description for year 2023" },
    { year: 2022, desc: "Description for year 2022" },
    { year: 2021, desc: "Description for year 2021" },
    { year: 2020, desc: "Description for year 2020" }
  ];

  const handleClick = (year) => {
    setSelectedYear(year);
    setOpenFolder(year.year);
  };

  return (
    <Flex mx="150px" mt="50px" align="flex-start">
      <Box>
        <Heading as="h2" mb="4" size="s">HISTORY</Heading>
        <Heading as="h2" mb="3" size="md">We're really proud</Heading>
        <SimpleGrid columns={3} spacing={4}>
          {years.map((yearData, index) => (
            <Box
              key={index}
              p="2"
              borderRadius="md"
              bg="gray.100"
              cursor="pointer"
              onClick={() => handleClick(yearData)}
              _hover={{ bg: "gray.200" }}
            >
              <Flex align="center">
                <Icon as={openFolder === yearData.year ? MdFolderOpen : MdFolder} boxSize="24px" mr="2" />
                <Text>{yearData.year}</Text>
              </Flex>
            </Box>
          ))}
        </SimpleGrid>
      </Box>
      {selectedYear && (
        <Box
          p="4"
          ml="8"
          mt="15px"
          borderRadius="md"
          bg="gray.100"
          w="100%"
          maxW="600px"
          minH="150px"
          display="flex"
          flexDirection="column"
          justifyContent="center"
          alignItems="flex-start"
        >
          <Heading as="h2" size="md">{selectedYear.year}</Heading>
          <Text mt="2">{selectedYear.desc}</Text>
        </Box>
      )}
    </Flex>
  );
}

export default YearSection;
