import React from 'react';
import {
  Box,
  Container,
  Text,
  VStack,
  Input,
  Button,
  Flex,
  Heading,
  useColorModeValue,
} from '@chakra-ui/react';

const events = [
  {
    id: 1,
    date: 'June 20, 2024',
    title: 'React Conference',
    description: 'Join us for a day of React talks and workshops with industry leaders.',
  },
  {
    id: 2,
    date: 'July 15, 2024',
    title: 'JavaScript Summit',
    description: 'A summit dedicated to all things JavaScript, from frameworks to tooling.',
  },
];

const SubscriptionSection = () => {
  return (
    <Box bgGradient="linear(to-b, white, gray.100)" py="50px">
      <Container maxW="8xl">
        <VStack spacing={4} textAlign="center">
          <Heading fontSize="lg" fontWeight="bold">
            Get to know what we're doing, first
          </Heading>
          <Text>Subscribe to our newsletter to stay updated with our latest events and news.</Text>
          <Flex
            as="form"
            onSubmit={(e) => {
              e.preventDefault();
              // handle subscription logic here
            }}
            w={{ base: '100%', md: '50%' }}
            direction={{ base: 'column', md: 'row' }}
            align="center"
          >
            <Input
              placeholder="Enter your email"
              type="email"
              required
              mb={{ base: 2, md: 0 }}
              mr={{ base: 0, md: 2 }}
            />
            <Button colorScheme="teal" type="submit">
              Subscribe
            </Button>
          </Flex>
        </VStack>
      </Container>
    </Box>
  );
};

const UpcomingEvents = () => {
  return (
    <Box bgGradient="linear(to-b, white, gray.200)" py="50px">
      <Container maxW="8xl">
        <Heading fontSize="lg" fontWeight="bold" mb={10} textAlign="left">
          Upcoming Events
        </Heading>
        <Flex wrap="wrap" justifyContent="flex-start">
          {events.map((event) => (
            <EventCard key={event.id} {...event} />
          ))}
        </Flex>
      </Container>
    </Box>
  );
};

const EventCard = ({ date, title, description }) => {
  return (
    <Box
      w={{ base: '100%', md: '45%', lg: '30%' }}
      bg={useColorModeValue('white', 'gray.700')}
      boxShadow="lg"
      rounded="lg"
      p={6}
      m={4}
      textAlign="left"
    >
      <Text fontSize="lg" color="teal.500" mb={2}>
        {date}
      </Text>
      <Heading as="h3" fontSize="lg" mb={2}>
        {title}
      </Heading>
      <Text fontSize="md">{description}</Text>
    </Box>
  );
};

const App = () => (
  <div>
    <SubscriptionSection />
    <UpcomingEvents />
  </div>
);

export default App;
