import React, { useEffect, useState } from 'react';
import { Box, Heading, Flex, Text } from '@chakra-ui/react';

const Timeline = () => {
  const timelineEvents = [
    { title: 'Announcement', date: 'January 1, 2024' },
    { title: 'Registration Opens', date: 'February 1, 2024' },
    { title: 'Submission Deadline', date: 'March 31, 2024' },
    { title: 'Judging Period', date: 'April 1 - April 15, 2024' },
    { title: 'Winners Announced', date: 'April 20, 2024' }
  ];

  const [timelineHeight, setTimelineHeight] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      const scrollY = window.scrollY + window.innerHeight;
      const timelineOffsetTop = document.getElementById('timeline').offsetTop;
      const heightToFill = scrollY - timelineOffsetTop;
      setTimelineHeight(heightToFill > 0 ? heightToFill : 0);
    };

    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <Box id="timeline" py={12} px={8}>
      <Heading mb={8}>Event Timeline</Heading>
      <Box position="relative" maxWidth="1000px" margin="0 auto">
        <Box
          position="absolute"
          width="6px"
          bg="gray.800"
          left="50%"
          transform="translateX(-50%)"
          height={`${timelineHeight}px`}
        />
        {timelineEvents.map((event, index) => (
          <Flex key={index} mb={8} align="center" justify={index % 2 === 0 ? 'flex-start' : 'flex-end'}>
            <Box
              bg="gray.800"
              color="white"
              p={4}
              borderRadius="md"
              width="45%"
              position="relative"
              ml={index % 2 !== 0 ? 'auto' : 0}
            >
              <Text fontSize="lg" fontWeight="bold">{event.title}</Text>
              <Text mt={2}>{event.date}</Text>
              <Box
                position="absolute"
                width="25px"
                height="25px"
                bg="white"
                border="4px solid gray.800"
                borderRadius="50%"
                top="50%"
                transform="translateY(-50%)"
                right={index % 2 === 0 ? '-15px' : 'auto'}
                left={index % 2 !== 0 ? '-15px' : 'auto'}
              />
            </Box>
          </Flex>
        ))}
      </Box>
    </Box>
  );
};

export default Timeline;
