import React from 'react';
import { Box, Flex, Heading, Text, Icon, useColorModeValue } from '@chakra-ui/react';
import { MdEvent, MdHistory, MdDateRange } from 'react-icons/md';

const ProgramTag = ({ icon, title, desc }) => {
  const hoverBgColor = useColorModeValue("gray.200", "gray.700"); // Adjusting hover background color based on color mode

  return (
    <Box
      p="4"
      borderRadius="lg"
      textAlign="center"
      m="2"
      bg={useColorModeValue("gray.100", "gray.800")} // Adjusting background color based on color mode
      _hover={{ bg: hoverBgColor, cursor: "pointer" }} // Applying hover animation and cursor pointer
    >
      <Icon as={icon} boxSize="24px" mb="2" />
      <Heading size="md" mb="2">{title}</Heading>
      <Text fontSize="sm" color="gray.600">{desc}</Text>
    </Box>
  );
}

const ProgramsSection = () => {
  return (
    <Flex direction="column" align="center">
      <Flex justify="space-between" w="80%">
        <ProgramTag
          icon={MdEvent}
          title="Upcoming"
          desc="Discover events happening soon."
        />
        <ProgramTag
          icon={MdHistory}
          title="Past"
          desc="Explore past events and their outcomes."
        />
        <ProgramTag
          icon={MdDateRange}
          title="Year"
          desc="Check out events by year."
        />
      </Flex>
    </Flex>
  );
}

export default ProgramsSection;
