import React from 'react';
import {
  Box,
  Container,
  Text,
  VStack,
  Input,
  Button,
  Grid, // Import Grid component
  Heading,
} from '@chakra-ui/react';
import { useForm, ValidationError } from '@formspree/react';

const SubscriptionSection = () => {
  const [state, handleSubmit] = useForm("mayrgkba"); // Replace "mayrgkba" with your actual Formspree form ID

  return (
    <Box bgGradient="linear(to-b, white, gray.100)" py="50px">
      <Container maxW="8xl">
        <VStack spacing={4} textAlign="center">
          <Heading fontSize="lg" fontWeight="bold">
            Get to know what we're doing, first
          </Heading>
          <Text>Subscribe to our newsletter to stay updated with our latest events and news.</Text>
          {state.succeeded ? (
            <Text color="green.500">We've received your email and we will not spam!</Text>
          ) : (
            <form onSubmit={handleSubmit}>
              <Grid templateColumns="1fr" gap={2} w="100%" maxW="md"> {/* Grid setup */}
                <Input
                  placeholder="Enter your email"
                  type="email"
                  name="email"
                  required
                />
              </Grid>
              <Grid templateColumns="1fr" gap={2} w="100%" maxW="md"> {/* Grid setup */}
                <Button
                  colorScheme="teal"
                  type="submit"
                  w="full"
                  isLoading={state.submitting}
                >
                  {state.submitting ? 'Subscribing...' : 'Subscribe'}
                </Button>
              </Grid>
              <ValidationError
                prefix="Email"
                field="email"
                errors={state.errors}
              />
            </form>
          )}
        </VStack>
      </Container>
    </Box>
  );
};

export default SubscriptionSection;
