import React from 'react';
import {
  Box,
  Container,
  Text,
  HStack,
  VStack,
  Flex,
  useColorModeValue,
  useBreakpointValue,
  Heading, // Correct import for Heading component
} from '@chakra-ui/react';

const milestones = [
  {
    id: 1,
    date: 'September 2020',
    title: 'Nodebrew Founded',
    description: 'Nodebrew was founded with one mission: to empower underrepresented communities'
  },
  {
    id: 2,
    date: 'March 2021',
    title: 'Startup Pitch Launched',
    description: 'After 5 months of preparation, our first event was launched on Devpost which experienced a footfall of 350 women across the world'
  },
  {
    id: 3,
    date: 'July 2021',
    title: 'First Hackathon Launched',
    description:
      'Taking over the success of the startup pitch, we organized our first hackathon with 400 hackers and the rest is history',
  },
  {
    id: 4,
    date: 'September 2021',
    title: "India's Largest All-Women Hackathon",
    description:
      'Hack Wizard was organized while onboarding prominent sponsors like Balsamiq and Jane Street to become the largest all-women hackathon in India with 700 participants'
  },
  {
    id: 5,
    date: 'November 2022',
    title: "MLH Season Hackathon",
    description:
      'Hack Concode became MLH Season 2022 hackathon and bought a footfall of 800 hackers from 26 countries'
  },
  {
    id: 6,
    date: 'Febraury 2024',
    title: "Flagship Hackathon: Hack Brew",
    description:
      'Nodebrew rebranded to present the best hackathon of the season-Hack Brew, with the best mentors, resources and prizes and is our flagship hackathon'
  },
];

const Milestones = () => {
  const isMobile = useBreakpointValue({ base: true, md: false });
  const isDesktop = useBreakpointValue({ base: false, md: true });

  return (
    <Box mx={{ base: "20px", md: "150px" }} mt="50px">
    <Container maxWidth="6xl">
      <Heading size="md" fontWeight="bold" mb={8} textAlign="left">Milestones</Heading>
      {milestones.map((milestone) => (
        <Flex key={milestone.id} mb={isMobile ? "20px" : "10px"} direction={isMobile ? "column" : "row"}>
          {/* Mobile view */}
          {isMobile && (
            <LineWithDot />
          )}
          <Card {...milestone} />
          {/* Desktop view */}
          {isDesktop && <LineWithDot />}
        </Flex>
      ))}
    </Container>
    </Box>
  );
};

interface CardProps {
  id: number;
  title: string;
  description: string;
  date: string;
}

const Card = ({ id, title, description, date }: CardProps) => {
  return (
    <VStack
      flex={{ base: 1, md: 2 }}
      p={{ base: 3, sm: 6 }}
      bg={useColorModeValue('gray.100', 'gray.800')}
      spacing={5}
      rounded="lg"
      alignItems="stretch"
      textAlign="left"
    >
      <Text fontSize="lg" color="blue.400" mb="1">{date}</Text>
      <Heading as="h1" fontSize="lg" fontWeight="bold" lineHeight={1.2} mb="1">{title}</Heading>
      <Text fontSize="md" mb="1">{description}</Text>
    </VStack>
  );
};

const LineWithDot = () => {
  return (
    <Flex
      direction="column"
      alignItems="center"
      mr={{ base: '0', md: '40px' }}
      ml={{ base: '0', md: '40px' }}
    >
      <Box
        position="relative"
        height="100%"
        borderRight={{ base: 'none', md: '1px solid' }}
        borderColor={useColorModeValue('gray.200', 'gray.700')}
      >
        <Box
          pos="relative"
          p="10px"
          bg={useColorModeValue('gray.600', 'gray.200')}
          borderRadius="100px"
          opacity={1}
        ></Box>
      </Box>
    </Flex>
  );
};

export default Milestones;
