import React from 'react';
import { Box, Flex, Heading, Text, useColorModeValue } from '@chakra-ui/react';
import MetricsSection from './MetricsSection'; // Assuming you have the MetricsSection component

function AboutSection() {
  const bgColor1 = "#F5F5F5"; // Gray background color
  const bgColor2 = "white"; // White background color

  return (
    <Flex p="4">
      <Box flex="1" bg={bgColor1} borderRadius="lg">
        <Box p="4">
          <Heading as="h2" size="lg" mb="4">About</Heading>
          <Heading as="h3" size="md" mb="2">Our Story</Heading>
          <Text mb="4">
            Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nulla ut nisi vitae elit gravida rhoncus. 
            Donec sit amet nibh nec urna dignissim tempor. Quisque euismod arcu vel massa pulvinar, at ultrices 
            elit congue. Pellentesque habitant morbi tristique senectus et netus et malesuada fames ac turpis egestas.
          </Text>
        </Box>
      </Box>
      <Box flex="1" bg={bgColor2} borderRadius="lg">
        <MetricsSection />
      </Box>
    </Flex>
  );
}

export default AboutSection;
