import React from 'react';
import { Box, Flex, Heading, Avatar, Text, SimpleGrid } from '@chakra-ui/react';

const teamMembers = [
  { name: 'John Doe', position: 'CEO', avatar: 'https://bit.ly/broken-link' },
  { name: 'Jane Smith', position: 'CTO', avatar: 'https://bit.ly/broken-link' },
  { name: 'Emily Johnson', position: 'CFO', avatar: 'https://bit.ly/broken-link' },
  { name: 'Michael Brown', position: 'COO', avatar: 'https://bit.ly/broken-link' },
  { name: 'Sarah Davis', position: 'CMO', avatar: 'https://bit.ly/broken-link' },
  { name: 'David Wilson', position: 'CIO', avatar: 'https://bit.ly/broken-link' }
];

const TeamMember = ({ name, position, avatar }) => (
  <Box textAlign="center" p="4">
    <Avatar size="xl" src={avatar} mb="4" />
    <Heading as="h3" size="md" mb="2">{name}</Heading>
    <Text fontSize="sm" color="gray.600">{position}</Text>
  </Box>
);

const MeetTheTeam = () => {
  return (
    <Box bg="#F5F5F5" py="10">
      <Heading as="h2" mb="8" textAlign="center">Meet the Team</Heading>
      <SimpleGrid columns={{ base: 1, md: 3 }} spacing={10} mx="150px">
        {teamMembers.map((member, index) => (
          <TeamMember
            key={index}
            name={member.name}
            position={member.position}
            avatar={member.avatar}
          />
        ))}
      </SimpleGrid>
    </Box>
  );
}

export default MeetTheTeam;
