import React, { useState } from 'react';
import { Box, Flex, Heading, Text, Icon, useColorModeValue } from '@chakra-ui/react';
import { MdFolder, MdFolderOpen } from 'react-icons/md';

const YearSection = () => {
  const [selectedYear, setSelectedYear] = useState(null);
  const [openFolders, setOpenFolders] = useState({});

  const years = [
    { year: 2024, desc: "Description for year 2024" },
    { year: 2023, desc: "Description for year 2023" },
    { year: 2022, desc: "Description for year 2022" },
    { year: 2021, desc: "Description for year 2021" },
    { year: 2020, desc: "Description for year 2020" }
  ];

  const handleClick = (year) => {
    setSelectedYear(year);
    setOpenFolders(prevState => ({
      ...prevState,
      [year.year]: !prevState[year.year]
    }));
  };

  return (
    <Flex direction="column">
      <Flex>
        {years.map((yearData, index) => (
          <Box key={index} mr="4" mb="4" p="2" borderRadius="md" bg="gray.100" cursor="pointer" onClick={() => handleClick(yearData)}>
            <Icon as={openFolders[yearData.year] ? MdFolderOpen : MdFolder} boxSize="24px" />
            <Text mt="2">{yearData.year}</Text>
          </Box>
        ))}
      </Flex>
      {selectedYear && (
        <Box p="4" borderRadius="md" bg="gray.100">
          <Heading as="h2" size="md">{selectedYear.year}</Heading>
          <Text mt="2">{selectedYear.desc}</Text>
        </Box>
      )}
    </Flex>
  );
}

export default YearSection;
