import React from 'react';
import { Box, Flex, Text, Icon, useColorModeValue } from '@chakra-ui/react';
import { CalendarIcon } from '@chakra-ui/icons';

const timelineData = [
  { year: '2020', event: 'Project Initiation' },
  { year: '2021', event: 'Development Phase' },
  { year: '2022', event: 'Testing and QA' },
  { year: '2023', event: 'Launch' },
  { year: '2024', event: 'Post-launch Support' },
];

function Timeline() {
  const lineColor = useColorModeValue('gray.300', 'gray.600');
  const iconColor = useColorModeValue('teal.500', 'teal.300');

  return (
    <Flex direction="column" alignItems="center">
      {timelineData.map((event, index) => (
        <Flex key={index} alignItems="center" mb="4">
          {/* Year */}
          <Text fontWeight="bold" mr="4">{event.year}</Text>

          {/* Dot Icon */}
          <Box
            borderRadius="full"
            bg={iconColor}
            w="8px"
            h="8px"
            zIndex={1}
            boxShadow="0 0 0 2px #fff"
          />

          {/* Vertical Line */}
          {index !== timelineData.length - 1 && (
            <Box bg={lineColor} w="1px" h="20px" ml="2" />
          )}

          {/* Event Text */}
          <Box ml="4">
            <Text fontWeight="bold">{event.event}</Text>
            <Icon as={CalendarIcon} color="teal.500" mt="1" />
          </Box>
        </Flex>
      ))}
    </Flex>
  );
}

export default Timeline;
