import React from 'react';
import { Box, Heading, Text, Accordion, AccordionItem, AccordionButton, AccordionPanel, AccordionIcon } from '@chakra-ui/react';

function InteractiveTimeline() {
  const events = [
    { year: 2020, title: 'Foundation', description: 'Founded the initiative to make tech accessible.' },
    { year: 2021, title: 'Community Outreach', description: 'Launched our first community outreach program.' },
    { year: 2022, title: 'Expansion', description: 'Expanded our efforts to include more communities.' },
    { year: 2023, title: 'Tech Conference', description: 'Held our first tech conference with industry leaders.' },
    { year: 2024, title: 'Milestone', description: 'Reached a milestone of 10,000 people impacted.' },
  ];

  return (
    <Box bgGradient="linear(to-b, white, gray.200)" py="50px" px="20px">
      <Box textAlign="center" mb="8">
        <Heading size="xl" fontWeight="bold" color="gray.700">Interactive Timeline</Heading>
      </Box>
      <Accordion allowToggle maxW="800px" mx="auto">
        {events.map((event, index) => (
          <AccordionItem key={index}>
            <h2>
              <AccordionButton _expanded={{ bg: "teal.500", color: "white" }}>
                <Box flex="1" textAlign="left">
                  <Heading size="md">{event.year}</Heading>
                  <Text fontSize="lg">{event.title}</Text>
                </Box>
                <AccordionIcon />
              </AccordionButton>
            </h2>
            <AccordionPanel pb={4}>
              <Text fontSize="md">{event.description}</Text>
            </AccordionPanel>
          </AccordionItem>
        ))}
      </Accordion>
    </Box>
  );
}

export default InteractiveTimeline;
