import React from 'react';
import { Box, Flex, Heading, Avatar, Text, Link, Icon, SimpleGrid } from '@chakra-ui/react';
import { FaLinkedin, FaGithub, FaGlobe } from 'react-icons/fa';

const teamMembers = [
  {
    name: 'John Doe',
    position: 'CEO',
    avatar: 'https://bit.ly/broken-link',
    linkedin: 'https://linkedin.com/in/johndoe',
    github: 'https://github.com/johndoe',
    website: 'https://johndoe.com'
  },
  {
    name: 'Jane Smith',
    position: 'CTO',
    avatar: 'https://bit.ly/broken-link',
    linkedin: 'https://linkedin.com/in/janesmith',
    github: 'https://github.com/janesmith',
    website: 'https://janesmith.com'
  },
  // Add more team members here
];

const TeamMember = ({ name, position, avatar, linkedin, github, website }) => (
  <Flex align="team" p="4" borderRadius="md" bg="gray.100" _hover={{ bg: "gray.200" }} mb="4">
    <Avatar size="xl" src={avatar} mr="4" />
    <Box>
      <Heading as="h3" size="md" mb="2">{name}</Heading>
      <Text fontSize="sm" color="gray.600" mb="2">{position}</Text>
      <Flex>
        <Link href={linkedin} isExternal mr="2">
          <Icon as={FaLinkedin} boxSize="5" />
        </Link>
        <Link href={github} isExternal mr="2">
          <Icon as={FaGithub} boxSize="5" />
        </Link>
        <Link href={website} isExternal>
          <Icon as={FaGlobe} boxSize="5" />
        </Link>
      </Flex>
    </Box>
  </Flex>
);

const MeetTheTeam = () => {
  return (
    <Box mx="150" py="10">
      <Heading as="h2" mb="8" textAlign="left">Meet the Team</Heading>
      <SimpleGrid columns={{ base: 1, md: 2 }} spacing={10} mx="150px">
        {teamMembers.map((member, index) => (
          <TeamMember
            key={index}
            name={member.name}
            position={member.position}
            avatar={member.avatar}
            linkedin={member.linkedin}
            github={member.github}
            website={member.website}
          />
        ))}
      </SimpleGrid>
    </Box>
  );
}

export default MeetTheTeam;
