import React from 'react';
import { Box, Flex, Spacer, Link, Menu, MenuButton, MenuList, MenuItem, useMediaQuery } from '@chakra-ui/react';

const Navbar = () => {
  const [isLargerThan768] = useMediaQuery("(min-width: 768px)");

  return (
    <>
      <Flex p="4" bg="#F5F5F5" alignItems="center" flexWrap="wrap"> {/* Added flexWrap="wrap" */}
        <Box mx="4" mb={isLargerThan768 ? "0" : "2"} width={{ base: "100%", sm: "auto" }}> {/* Adjusted spacing and width */}
          <img src="files/images/logo.png" alt="Logo" style={{ width: '80px' }} />
        </Box>
        <Spacer />
        <Box width={{ base: "100%", sm: "auto" }}> {/* Adjusted width */}
          <Flex alignItems="center">
            {isLargerThan768 && ( /* Display links only on screens larger than 768px */
              <>
                <Link mr="4" color="black" fontSize={{ base: "md", md: "lg" }} href="/">Home</Link> {/* Adjusted font size */}
                <Link mr="4" color="black" fontSize={{ base: "md", md: "lg" }} href="/about">About</Link> {/* Adjusted font size */}
                <Link mr="4" color="black" fontSize={{ base: "md", md: "lg" }} href="/services">Services</Link> {/* Adjusted font size */}
              </>
            )}
            <Menu>
              <MenuButton bg="#F5F5F5" color="black" fontSize={{ base: "md", md: "lg" }}>More</MenuButton> {/* Adjusted font size */}
              <MenuList bg="#F5F5F5"> {/* Adjusted background color */}
                <MenuItem bg="#F5F5F5" color="black" fontSize={{ base: "md", md: "lg" }} as={Link} href="/contact">Contact</MenuItem> {/* Adjusted font size */}
                <MenuItem bg="#F5F5F5" color="black" fontSize={{ base: "md", md: "lg" }} as={Link} href="/faq">FAQ</MenuItem> {/* Adjusted font size */}
              </MenuList>
            </Menu>
          </Flex>
        </Box>
      </Flex>
      <Box borderBottom="1px solid #ccc" />
    </>
  );
}

export default Navbar;
