import React from 'react';
import { Box, Heading, Text, VStack, Flex } from '@chakra-ui/react';

function Timeline() {
  const events = [
    { year: 2020, description: 'Founded the initiative to make tech accessible.' },
    { year: 2021, description: 'Launched our first community outreach program.' },
    { year: 2022, description: 'Expanded our efforts to include more communities.' },
    { year: 2023, description: 'Held our first tech conference with industry leaders.' },
    { year: 2024, description: 'Reached a milestone of 10,000 people impacted.' },
  ];

  return (
    <Box bgGradient="linear(to-b, white, gray.200)" py="50px">
      <Flex alignItems="center" justifyContent="center">
        <VStack spacing={6} textAlign="center" maxW="800px" mx="auto">
          <Heading size="xl" fontWeight="bold" color="gray.700" mb={8}>
            Timeline
          </Heading>
          {events.map((event, index) => (
            <Box key={index} borderLeft="4px solid teal" pl={4} textAlign="left" maxW="600px">
              <Heading size="md" color="teal.500">{event.year}</Heading>
              <Text fontSize="lg" color="gray.600">{event.description}</Text>
            </Box>
          ))}
        </VStack>
      </Flex>
    </Box>
  );
}

export default Timeline;
