import React, { useEffect, useState } from 'react';
import { Box, Flex, Heading, Text } from '@chakra-ui/react';

function MetricBox({ title, count, description }) {
  const [currentCount, setCurrentCount] = useState(0);

  useEffect(() => {
    const timer = setTimeout(() => {
      setCurrentCount(count);
    }, 1000);

    return () => clearTimeout(timer);
  }, [count]);

  return (
    <Box p="4" bg="gray.200" borderRadius="lg" textAlign="center">
      <Heading size="lg" mb="2" fontWeight="bold">
        {currentCount}
      </Heading>
      <Text fontSize="sm" color="gray.600" mb="2">{title}</Text>
      <Text fontSize="md">{description}</Text>
    </Box>
  );
}

function MetricsSection() {
  return (
    <Flex justify="space-between">
      <MetricBox title="Active Users" count={1000} description="Number of active users." />
      <MetricBox title="Sales" count={500} description="Total sales amount." />
      <MetricBox title="Subscribers" count={2000} description="Number of newsletter subscribers." />
      <MetricBox title="Downloads" count={300} description="Total app downloads." />
    </Flex>
  );
}

export default MetricsSection;
