import React from 'react';
import {
  Box,
  Container,
  Text,
  VStack,
  Flex,
  Heading,
  useColorModeValue,
} from '@chakra-ui/react';

const events = [
  {
    id: 1,
    date: 'June 20, 2024',
    title: 'React Conference',
    description: 'Join us for a day of React talks and workshops with industry leaders.',
  },
  {
    id: 2,
    date: 'July 15, 2024',
    title: 'JavaScript Summit',
    description: 'A summit dedicated to all things JavaScript, from frameworks to tooling.',
  },
  {
    id: 3,
    date: 'August 10, 2024',
    title: 'Web Development Bootcamp',
    description: 'An intensive bootcamp covering modern web development practices and techniques.',
  },
];

const UpcomingEvents = () => {
  return (
    <Box bgGradient="linear(to-r, gray.300, gray.500)" py={10}>
      <Container maxWidth="7xl" p={4}>
        <Heading fontSize="3xl" fontWeight="bold" mb={8} textAlign="center">
          Upcoming Events
        </Heading>
        <VStack spacing={8}>
          {events.map((event) => (
            <EventCard key={event.id} {...event} />
          ))}
        </VStack>
      </Container>
    </Box>
  );
};

const EventCard = ({ date, title, description }) => {
  return (
    <Box
      w="full"
      bg={useColorModeValue('white', 'gray.700')}
      boxShadow="lg"
      rounded="lg"
      p={6}
      textAlign="left"
    >
      <Text fontSize="lg" color="teal.500" mb={2}>
        {date}
      </Text>
      <Heading as="h3" fontSize="2xl" mb={2}>
        {title}
      </Heading>
      <Text fontSize="md">{description}</Text>
    </Box>
  );
};

const App = () => (
  <div>
    <UpcomingEvents />
  </div>
);

export default App;
